<?php get_header(); ?><?php if (have_posts()) : while (have_posts()) : the_post(); ?>	<div class="prepend-1 span-13 append-1 prepend-top" id="single">		<div id="postheader"></div>		<h1><?php the_title(); ?></h1>		<?php the_content(); ?>		<?php wp_link_pages( array( 'before' => '<div class="page-link"><span>' . __( 'Pages:', 'hephaestus' ) . '</span>', 'after' => '</div>' ) ); ?>		<div class="clear"></div>	</div><?php endwhile; ?><?php else : ?><p><?php _e('Sorry, no posts matched your criteria.', 'hephaestus'); ?></p><?php endif; ?><?php get_sidebar(); ?><?php get_footer(); ?>